import React, { useState, useEffect } from "react"; 
import styled from 'styled-components';
import axios from 'axios';
import ReactDOM from "react-dom";
import { withFormik, Form, Field, ErrorMessage } from 'formik';

const Div = styled.div`
    text-align: center;
    background-color: #171330;
    color: #FFF2F2;
    font-family: Muli;
    width: 50%;
    margin: auto;
    padding: 2.5rem 3rem 2.5rem 5rem;
    border: 5px solid #fa4252;
  `;
const Ul = styled.div`
list-style-type:none;
line-height: 2;
padding: 2rem 3.5rem;

`

const Span = styled.div`
display: none;
`


const LoginForm = props => {

    const [Loginuser, setLoginUser] = useState({
        username: "", 
        Password: ""

    });

    const [Login, setLogin] = useState("");
      useEffect(() => {
       axios
        .get("https://weightlifting-journal.herokuapp.com/")
        .then(res => setLogin(res.data.message))
        .catch(err => console.log(err));
    },[]);


    const handleChanges = event => {
        setLoginUser({ ...Loginuser, [event.target.name]: event.target.value});
    };

    const submitForm = event => {
        event.preventDefault();
        props.addNewUser(Loginuser);
        setLoginUser({ username: "", email: "", password: ""});
    };

    return(
        <Div>
        <form onSubmit={submitForm}>
            <h2>Log In</h2>
            <fieldset>
            <Ul>
                <li>
             <label htmlFor= "username">Username</label>
                <input 
                id= "username"
                type="text"
                name="username"
                placeholder="username"
                onChange={handleChanges}
                value={Loginuser.username}

                />
            </li>
            <li>
            <label htmlFor= "password">Password</label>
                <input
                id= "password"
                type="text"
                name="password"
                placeholder="password"
                onChange={handleChanges}
                value={Loginuser.role}/>
                </li>
        </Ul>
            </fieldset>

            <button type='submit'>Log In</button>

        </form>
        axios;

        </Div>
    );
}


export default LoginForm;
