import React, { useState } from "react"; 
import styled from 'styled-components';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';

const Div = styled.div`
    text-align: center;
    background-color: #171330;
    color: white;
    font-family: Muli;
    width: 50%;
    margin: auto;
    padding: 2.5rem 3rem 2.5rem 5rem;
    border: 5px solid #fa4252;
  `;

  const Ul = styled.div`
    list-style-type:none;
    line-height: 2;
    padding: 2rem 3.5rem;

    `;

const H2 = styled.div`
      color: white;


`

const Schema = Yup.object().shape({
    username: Yup.string()
        .required('Enter your username'),
    password: Yup.string()
        .required('Enter your password')
  });

const Register = props => {
    const [registerData, setRegisterData] = useState({
        username: "", 
        Password: ""

    });

    const handleChanges = event => {
        setRegisterData({ ...registerData, [event.target.name]: event.target.value});
    };

    const submitForm = event => {
        event.preventDefault();
        props.addNewUser(registerData);
        setRegisterData({ username: "", password: ""});
    };

    return(
        <div className='form-login'>
            <h2>Register</h2>
          <Formik
            initialValues={{
                username: '',
                password: ''
            }}
            validationSchema={Schema}
            onSubmit={(values, tools) =>{
                console.log(values);
                axios.post('https://symphinity-backend.herokuapp.com//api/auth/register', values)
                .then(res => console.log(res))
                .catch(err => console.log(err));
                tools.resetForm();
                props.history.push('/login');
            }}
            >
                {({errors, touched, isSubmitting}) => 
          <form onSubmit={submitForm}>

            <fieldset>
            <legend>Create Account</legend>
            <Ul>
                <li>
                  <label htmlFor= "username">Username</label>
                    <input 
                       id= "username"
                       type="text"
                       name="username"
                       placeholder="username"
                       onChange={handleChanges}
                       value={registerData.username}
                    />
                </li>


               <li>
                    <label htmlFor= "password">Password </label>
                        <input
                        id= "password"
                        type="text"
                        name="password"
                        placeholder="password"
                        onChange={handleChanges}
                        value={registerData.role}/>
                </li>
            </Ul>
            </fieldset>

                <button type='submit'>Submit</button>

                <button type="button">Have an Account?</button>

        </form>
        }
        </Formik>
       </div>
    );
};
export default Register;
