import React, { useState } from "react"; 
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import styled from 'styled-components';

const Div = styled.div`
    text-align: center;
    background-color: #171330;
    color: white;
    font-family: Muli;
    width: 50%;
    margin: auto;
    padding: 2.5rem 3rem 2.5rem 5rem;
    border: 5px solid #fa4252;
  `;



const Register = props => {
    const [registerData, setRegisterData] = useState({
        username: "", 
        Password: ""

    });

    return(
        <Div className='form-login'>
            <h2>Register</h2>
          <Formik
          onSubmit={(values, tools) =>{
            // console.log(values);
            // axios.post('https://symphinity-backend.herokuapp.com//api/auth/register', values)
            // .then(res => console.log(res))
            // .catch(err => console.log(err));
            // tools.resetForm();
            // props.history.push('/login');
        }}
            initialValues={{
                username: '',
                password: ''
            }}


            >
                {({errors, touched, isSubmitting}) => 
                  <Form>
                    <div className='input-container'>
                            <ion-icon name="person"></ion-icon>
                            <div className='input-field'>
                              <label htmlFor= "username">Username</label>
                                <input 
                                id= "username"
                                type="text"
                                name="username"
                                placeholder="username"
                                // onChange={handleChanges}
                                // value={registerData.username}
                                />
                              </div>
                    </div>

                <div className='input-container'>
                        <ion-icon name="lock"></ion-icon>
                        <div className='input-field'>

                          <label htmlFor= "password">Password </label>
                                <input
                                id= "password"
                                type="text"
                                name="password"
                                placeholder="password"
                                // onChange={handleChanges}
                                // value={registerData.role
                                />
                         </div>
                    </div>

           <div>
                <button type='submit' disabled={isSubmitting}>Submit</button>

                <Link to='/login'><button>Login</button></Link>

           </div>
           {errors.username && touched.username ? <div style={{color: 'red'}}>{errors.username}</div> : null}
           {errors.password && touched.password ? <div style={{color: 'red'}}>{errors.password}</div> : null}
           </Form>
        }
        </Formik>
       </Div>
    );
};
export default Register;
